/* Based on the docs, Enable WAL journal mode when you create a new database to improve performance in general. */
/* https://docs.expo.dev/versions/latest/sdk/sqlite/ */
-- PRAGMA journal_mode = WAL
-- PRAGMA foreign_keys = ON

DROP TABLE IF EXISTS `beach`;
CREATE TABLE `beach` (
  `id` varchar(128) NOT NULL,
  `name` varchar(255) NOT NULL,
  `municipality` varchar(255) NOT NULL,
  `islandGroup` varchar(255) NOT NULL,
  `country` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `Type` int NOT NULL DEFAULT (1),
  `created_at` datetime DEFAULT CURRENT_TIMESTAMP,
  `regionId` varchar(128) DEFAULT NULL,
  `provinceId` varchar(128) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `regionId` (`regionId`),
  KEY `provinceId` (`provinceId`),
  CONSTRAINT `beach_ibfk_1` FOREIGN KEY (`regionId`) REFERENCES `region` (`id`),
  CONSTRAINT `beach_ibfk_2` FOREIGN KEY (`provinceId`) REFERENCES `province` (`id`)
);



DROP TABLE IF EXISTS `province`;
CREATE TABLE `province` (
  `id` varchar(128) NOT NULL,
  `regionId` varchar(128) NOT NULL,
  `name` varchar(255) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `regionId` (`regionId`),
  CONSTRAINT `province_ibfk_1` FOREIGN KEY (`regionId`) REFERENCES `region` (`id`)
);


DROP TABLE IF EXISTS `region`;
CREATE TABLE `region` (
  `id` varchar(128) NOT NULL,
  `name` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
);


DROP TABLE IF EXISTS `snap`;
CREATE TABLE `snap` (
  `id` varchar(128) NOT NULL,
  `beachId` varchar(128) NOT NULL,
  `photoUrl` varchar(128) NOT NULL,
  `caption` varchar(255) NOT NULL,
  `dateVisited` datetime DEFAULT CURRENT_TIMESTAMP
  PRIMARY KEY (`id`),
  KEY `beachId` (`beachId`),
  CONSTRAINT `beach_ibfk_1` FOREIGN KEY (`beachId`) REFERENCES `beach` (`id`)
);